import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";
import axios from "axios";
import {Link} from "react-router-dom";

function Profile() {
    const {userInfo, error} = useSelector(state => state.user);
    const [user, setUser] = useState({});

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [rePassword, setRePassword] = useState("");

    const [address, setAddress] = useState("");
    const [city, setCity] = useState("");
    const [postal, setPostal] = useState("");
    const [country, setCountry] = useState("");

    useEffect(() => {
        const populateUserInfo = async () => {
            const {data} = await axios.get("api/users/" + userInfo._id);
            setUser(data)
            setName(data.name)
            setEmail(data.email)
            setPassword(data.password)
            setRePassword(data.password)
        }
        populateUserInfo();
    }, [])

    const handleSubmit = async (e) => {
        e.preventDefault()

        await axios.put("/api/users/" + userInfo._id + "/addShipping", {address, city, postal, country});
        //
        // dispatch({
        //     type: "create_shipping_info",
        //     payload: {address, city, postal, country}
        // })
        // props.history.push("/payment")
    }

    return <div className="container mt-4">
        <div className="row">
            <div className="col-6">
                <div className="card mt-4">
                    <div className="card-header">
                        <h5>Profile</h5>
                    </div>
                    <div className="card-body">
                        <form onSubmit={handleSubmit}>
                            {error && <div className="form-group alert alert-danger">{error}</div>}
                            <div className="form-row">
                                <div className="form-group col-5">
                                    <label htmlFor="exampleFormControlInput1">Name</label>
                                    <input type="text" className="form-control" required value={name} onChange={(e) => {setName(e.target.value)}}/>
                                </div>
                                <div className="form-group col-7">
                                    <label htmlFor="exampleFormControlInput1">Email</label>
                                    <input type="email" className="form-control" required value={user.email} onChange={(e) => {setEmail(e.target.value)}}/>
                                </div>
                            </div>

                            <div className="form-row">
                                <div className="form-group col-6">
                                    <label htmlFor="password1">Password</label>
                                    <input type="password" id="password1" className="form-control" value={user.password} required onChange={(e) => {setPassword(e.target.value)}}/>
                                </div>
                                <div className="form-group col-6">
                                    <label htmlFor="password2">Re-type Password</label>
                                    <input type="password" id="password2" className="form-control" value={user.password} required onChange={(e) => {setRePassword(e.target.value)}}/>
                                </div>
                            </div>
                            {
                                user.shipping ?
                                    <div>
                                        <div className="form-group">
                                            <label htmlFor="address">Address</label>
                                            <input type="text" id="address" className="form-control" value={user.shipping.address} required onChange={(e) => {setAddress(e.target.value)}}/>
                                        </div>

                                        <div className="form-row">
                                            <div className="form-group col-4">
                                                <label htmlFor="city">City</label>
                                                <input type="text" id="city" className="form-control" value={user.shipping.city} required onChange={(e) => {setCity(e.target.value)}}/>
                                            </div>

                                            <div className="form-group col-4">
                                                <label htmlFor="country">Country</label>
                                                <input type="text" id="country" className="form-control" value={user.shipping.country} required onChange={(e) => {setCountry(e.target.value)}}/>
                                            </div>

                                            <div className="form-group col-4">
                                                <label htmlFor="postal">Postal</label>
                                                <input type="text" id="postal" className="form-control" value={user.shipping.postal} required onChange={(e) => {setPostal(e.target.value)}}/>
                                            </div>
                                        </div>
                                    </div>
                                : null
                            }
                            <div className="form-group">
                                <button className="btn btn-primary btn-block" type="submit">Update</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>

            <div className="col-4">
                {/*<table className="table text-center">*/}
                {/*    <thead style={{border: "none"}}>*/}
                {/*    <tr>*/}
                {/*        <th style={{border: "none"}} scope="col"></th>*/}
                {/*        <th style={{border: "none"}} scope="col">Name</th>*/}
                {/*        <th style={{border: "none"}} scope="col">Price</th>*/}
                {/*        <th style={{border: "none"}} scope="col">Qty</th>*/}
                {/*        <th style={{border: "none"}} scope="col">Subtotal</th>*/}
                {/*        <th style={{border: "none"}} scope="col"></th>*/}
                {/*    </tr>*/}
                {/*    </thead>*/}
                {/*    {*/}
                {/*        itemsHtml.length > 0 ?*/}
                {/*            <tbody>*/}
                {/*            {itemsHtml}*/}
                {/*            </tbody>*/}
                {/*            :*/}
                {/*            <tbody>*/}
                {/*            <tr className="text-center">*/}
                {/*                <th className="text-center" scope="row" colSpan="6">Your cart is empty. </th>*/}
                {/*            </tr>*/}
                {/*            </tbody>*/}
                {/*    }*/}
                {/*</table>*/}
            </div>
        </div>
    </div>
}

export default Profile
