import React, {Component} from 'react';
import Link from "react-router-dom/modules/Link";

class Header extends Component{
    toggleSidebar = () => {
        let sidebar = document.getElementById("sidebar");
        if(sidebar.classList.contains("open")){
            sidebar.classList.remove('open')
        }
        else{
            sidebar.classList.add('open')
        }
    }
    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-light bg-light shadow">
                    <i role="button" className="ml-4 fas fa-bars fa-lg" onClick={this.toggleSidebar}></i>
                    <Link to="/"><a className="ml-4 navbar-brand" href="#">E-Commerce</a></Link>
                    <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
                        <ul className="navbar-nav ml-auto mt-2 mt-lg-0">
                            <li className="nav-item mr-4">
                                <a className="nav-link" href="#">Cart <span className="sr-only">(current)</span></a>
                            </li>
                            <li className="nav-item mr-4">
                                <a className="nav-link" href="#">Sign In</a>
                            </li>
                        </ul>
                    </div>
                </nav>

                <aside id="sidebar">
                    <p>first row</p>
                    <p>first row</p>
                    <p>first row</p>
                </aside>
            </div>
        )
    }
}

export default Header;
