function userReducer(state = {}, action) {
    if(action.type === "user_signin_success") {
        return {userInfo: action.payload}
    }
    else if(action.type === "user_signin_failed"){
        return {error: action.payload}
    }
    else {
        return state;
    }
}

export default userReducer
