import React from 'react';
import {Link} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import Cookie from "js-cookie"

function Header (props) {
    const toggleSidebar = () => {
        let sidebar = document.getElementById("sidebar");
        if(sidebar.classList.contains("open")){
            sidebar.classList.remove('open')
        }
        else{
            sidebar.classList.add('open')
        }
    }

    const {userInfo} = useSelector(state => state.user)
    const dispatch = useDispatch();

    const handleLogout = () => {
        Cookie.remove("userInfo");
        dispatch({
            type: "user_logout"
        })
    }

    return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-light bg-light shadow">
                    <i role="button" className="ml-4 fas fa-bars fa-lg" onClick={toggleSidebar}></i>
                    <Link to="/" className="ml-4 navbar-brand">E-Commerce</Link>
                    <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
                        <ul className="navbar-nav mr-4 mt-2 mt-lg-0">

                        </ul>

                        <ul className="navbar-nav ml-auto mr-4 mt-2 mt-lg-0">
                            <li className="nav-item">
                                <Link to="/?category=cartoon" style={{textDecoration:"none"}}>Cartoon</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/?category=movies" style={{textDecoration:"none"}}>Movies</Link>
                            </li>
                            
                            <li className="nav-item mr-4">
                                <Link to="/cart" className="nav-link">
                                    <i className="fas fa-shopping-cart mr-2"></i>
                                    Cart
                                </Link>
                            </li>
                            {
                                userInfo ?
                                <li className="nav-item dropdown">
                                    <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown"
                                       role="button" data-toggle="dropdown" aria-haspopup="true"
                                       aria-expanded="false">
                                        {userInfo.name}
                                    </a>
                                    <div className="dropdown-menu dropdown-menu-right" aria-labelledby="navbarDropdown">
                                        <Link className="dropdown-item" to="/profile">My Profile</Link>
                                        <Link className="dropdown-item" to="/orders">My Orders</Link>
                                        <div className="dropdown-divider"></div>
                                        <a className="dropdown-item" href="#">
                                            <button className="btn btn-danger btn-block" onClick={handleLogout}>Logout</button>
                                        </a>
                                    </div>
                                </li>
                                :
                                <li className="nav-item mr-4">
                                    <Link to="/signin" className="nav-link">Sign In</Link>
                                </li>
                            }
                        </ul>
                    </div>
                </nav>
            </div>
        )
}

export default Header;
