import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import axios from "axios"
import Cookie from "js-cookie"
import {Link} from "react-router-dom";

function Shipping(props) {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [rePassword, setRePassword] = useState("");
    const redirect = props.location.search ? props.location.search.split("=")[1] : "/";

    const {userInfo, error} = useSelector(state => state.user)
    const dispatch = useDispatch();

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            if(password !== rePassword){
                throw {message: "Passwords don't match. "}
            }

            const {data} = await axios.post("/api/users/register", {name, email, password});
            dispatch({
                type: "user_register_success",
                payload: data
            });

            Cookie.set("userInfo", data);
        }
        catch (e) {
            console.log(e)
            dispatch({
                type: "user_register_failed",
                payload: e.message
            });
        }
    }

    useEffect(() => {
        if(userInfo){
            props.history.push(redirect);
        }
    }, [userInfo]);

    return <div className="container mt-4 d-flex justify-content-center">
        <div className="card" style={{width: "22rem"}}>
            <div className="card-header">
                <h4>Shipping</h4>
            </div>
            <div className="card-body">
                <form onSubmit={handleSubmit}>
                    {error && <div className="form-group alert alert-danger">{error}</div>}
                    <div className="form-group">
                        <label htmlFor="exampleFormControlInput1">Name</label>
                        <input type="text" className="form-control" required onChange={(e) => {setName(e.target.value)}}/>
                    </div>
                    
                    <div className="form-group">
                        <button className="btn btn-primary btn-block" type="submit">Continue</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
}

export default Shipping
