import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import axios from "axios";
import Link from "react-router-dom/modules/Link";

function Cart(props) {
    const productId = props.match.params.id;
    const qty = props.location.search ? Number(props.location.search.split("=")[1]) : 1;
    const dispatch = useDispatch();

    useEffect(() => {
        const addToCart = async () => {
            const {data} = await axios.get("/api/products/" + productId);

            dispatch({
                type: "add_to_cart",
                product: data,
                qty: qty
            });
        }
        addToCart();
    })

    const items = useSelector(state => state.cart.products);

    const deleteItem = (productId) => {
        dispatch({
            type: "delete_from_cart",
            productId: productId
        });
    }

    const itemsHtml = items.map(item => {
        return <tr>
                <th scope="row">
                    <Link to="">
                        <img src={"/images/" + item.image} alt="" style={{height: "100px", width: "auto"}}/>
                    </Link>
                </th>
                <td>{item.name}</td>
                <td>${item.price}</td>
                <td>{item.qty}</td>
                <td>${item.price * item.qty}</td>
                <td><button className="btn btn-info" onClick={() => {deleteItem(item._id)}}>Delete</button></td>
            </tr>
    })

    const calculateTotal = () => {
        let total = 0;

        items.forEach((item) => {
            total += item.qty * item.price;
        })

        return total;
    }

    return <div className="container mt-4">
                <table className="table text-center">
                    <thead style={{border: "none"}}>
                    <tr>
                        <th style={{border: "none"}} scope="col"></th>
                        <th style={{border: "none"}} scope="col">Name</th>
                        <th style={{border: "none"}} scope="col">Price</th>
                        <th style={{border: "none"}} scope="col">Qty</th>
                        <th style={{border: "none"}} scope="col">Subtotal</th>
                        <th style={{border: "none"}} scope="col"></th>
                    </tr>
                    </thead>
                    <tbody>
                        {itemsHtml}
                        <tr>
                            <th scope="row"></th>
                            <td></td>
                            <td></td>
                            <td></td>
                            <td><strong>Total: ${calculateTotal()}</strong></td>
                            <td><button className="btn btn-info">Checkout</button></td>
                        </tr>
                    </tbody>
                </table>
        </div>
}

export default Cart
