import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import axios from 'axios';

function ProductsDetail(props){
    const dispatch = useDispatch();
    useEffect( () => {
        const fetch = async () => {
            const {data} = await axios.get("/api/products/" + props.match.params.id);
            dispatch(
                {
                type: "product_detail_success",
                payload: data
            })
        }
        fetch();

        return () => {
        };
    }, [])

    const [qty, setQty] = useState(1);
    const {product} = useSelector((state) => {
        return state.productDetails
    });

    const changeQty = (e) => {
        let updatedQty = e.target.value;
        setQty(updatedQty);

        if(updatedQty > product.stock){
            document.getElementById("AddToCart").classList.add("disabled");
            document.getElementById("exceedStockAlert").style.display = "inline";
        }
        else {
            document.getElementById("AddToCart").classList.remove("disabled");
            document.getElementById("exceedStockAlert").style.display = "none";
        }
    }

    const addToCart = async (e) => {
        const {data} = await axios.get("/api/products/" + product._id);

        dispatch({
            type: "add_to_cart",
            payload: {
                productId: data._id,
                name: data.name,
                image: data.image,
                price: data.price,
                countInStock: data.countInStock,
                qty: qty
            }
        });

        props.history.push("/cart")
    }

    const getRatingHtml = (rating) => {
        let ratingHtml = [];
        for(let i = 0; i < rating; i++) {
            ratingHtml.push(<i className="fas fa-star" style={{color: "orange"}}></i>)
        }

        let emptyStars = 5 - rating;

        for(let i = 0; i < emptyStars; i++) {
            ratingHtml.push(<i className="far fa-star" style={{color: "orange"}}></i>)
        }

        return ratingHtml;
    }

    return <div>
        {product ?
            <div className="mt-5">
                <div className='container mt-2 offset-2'>
                    <div className="row">
                        <div className="col-4">
                            <img src={"/images/" + product.image} alt=""/>
                        </div>

                        <div className="ml-4 col-7">
                            <h3>
                                {product.name}
                            </h3>

                            <div>
                                {getRatingHtml(product.rating)}
                            </div>

                            <div className="mt-2">
                                {product.numberOfReviews} customer reviews
                            </div>

                            <div className="mt-2">
                                Price: ${product.price}
                            </div>
                            
                            <div className="mt-4">
                                <h5>Description:</h5>
                                <p>{product.description}</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div> : <div>Loading...</div>}
    </div>
}

export default ProductsDetail;
