function cartReducer(state = {products: []}, action) {
    if(action.type === "add_to_cart")
    {
        const exsitingProduct = state.products.find(product => product._id === action.product._id);

        if(exsitingProduct){
            exsitingProduct.qty += action.qty;
            return state;
        }
        else {
            const newItem = {...action.product, qty: action.qty}
            return {products: [...state.products, newItem]}
        }
    }
    else if(action.type === "delete_from_cart"){
        return {products: state.products.filter(product => product._id !== action.productId)};
    }
    else if(action.type === "create_shipping_info"){
        return {products: state.products.filter(product => product._id !== action.productId)};
    }
    else {
        return state;
    }
}

export default cartReducer;
