import React, {useEffect} from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';
import {useDispatch, useSelector} from "react-redux";

function Home (props) {
    const dispatch = useDispatch();
    useEffect( () => {
        const fetch = async () => {
            const {data} = await axios.get("/api/products");

            if(props.location && props.location.search){
                const category = props.location.search.split("=")[1];
                if(category==="cartoon"){
                    document.getElementById("cartoon").classList.add("active")
                }
                else if(category==="movie"){
                    document.getElementById("movies").classList.add("active")
                }
            }

            dispatch(
                {
                    type: "products_list_success",
                    payload: data
                }
            );
        }
        fetch();
        return () => {

        };
    }, [])

    const productsList = useSelector( (state) => {
        return state.productsList
    });
    const {products} = productsList;

    const productsHtml = products.map(product => {
        return (
            <div className="mb-5" style={{width: "33%"}} key={product._id}>
                <div className="card" style={{width: "18rem"}}>
                    <Link to={'/products/' + product._id}><img className="card-img-top" src={"/images/" + product.image}/></Link>
                    <div className="card-body">
                        <h5 className="card-title">
                            <Link to={'/products/' + product._id} style={{textDecoration: "none"}}>
                                {product.name}
                            </Link>
                        </h5>
                        <small className="card-text">{product.brand}</small>
                        <br/>
                        <strong className="card-text">${product.price}</strong>
                        <p>{product.numberOfReviews}</p>
                    </div>
                </div>
            </div>
        )
    })

    return (
        <div className="d-flex flex-wrap container mt-5" style={{flexWrap: "wrap"}}>
            {productsHtml}
        </div>
    )
}

export default Home;
