import React, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';

function Home (props) {
    const [products, setProducts] = useState([]);

    useEffect( () => {
        const fetch = async () => {
            let {data} = await axios.get("/api/products");
            if(props.location && props.location.search){
                const category = props.location.search.split("=")[1];

                data = data.filter(product => product.category === category);
            }
            setProducts(data)
        }

        fetch();
    }, [products])

    const productsHtml = products.map(product => {
        return (
            <div className="mb-5" style={{width: "33%"}} key={product._id}>
                <div className="card" style={{width: "18rem"}}>
                    <Link to={'/products/' + product._id}><img className="card-img-top" src={"/images/" + product.image}/></Link>
                    <div className="card-body">
                        <h5 className="card-title">
                            <Link to={'/products/' + product._id} style={{textDecoration: "none"}}>
                                {product.name}
                            </Link>
                        </h5>
                        <small className="card-text">{product.brand}</small>
                        <br/>
                        <strong className="card-text">${product.price}</strong>
                        <p>{product.numberOfReviews}</p>
                    </div>
                </div>
            </div>
        )
    })

    return (
        <div className="d-flex flex-wrap container mt-5" style={{flexWrap: "wrap"}}>
            {productsHtml}
        </div>
    )
}

export default Home;
