import React, {Component} from "react";
import products from "../products";
import {Link} from "react-router-dom";

class ProductsDetail extends Component{
    render() {
        const product_id = this.props.match.params.id;
        const product = products.products.find(product => product._id === product_id)

        return (
            <div className="mt-2">
                <Link to="/" className="ml-4">Back to results</Link>
                <div className='container mt-2'>
                    <div className="row">
                        <div className="col-4">
                            <img src={"/images/" + product.image} alt=""/>
                        </div>
                        <div className="col-4">
                            <h3>
                                {product.name}
                            </h3>

                            <div>
                                {product.rating}
                            </div>

                            <div>
                                {product.numberOfReviews} customer reviews
                            </div>

                            <div>
                                Price: ${product.price}
                            </div>

                            <div>
                                State: In stock
                            </div>

                            <div>
                                <label htmlFor="Qty">Qty: </label>
                                <input className="ml-2" type="number" name="Qty" min="1" max="5"/>
                            </div>

                            <button className="btn btn-outline-secondary">Add to cart</button>

                            <div>
                                Description:
                            </div>
                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

export default ProductsDetail;
