import React, {Component, useEffect} from "react";
import {Link} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import axios from 'axios';

function ProductsDetail(props){
    const dispatch = useDispatch();
    useEffect( () => {
        const fetch = async () => {
            const {data} = await axios.get("/api/products/" + this.props.match.params.id);
            dispatch({
                type: "product_detail_success",
                payload: data
            })
        }
        fetch();
    })

    const product = useSelector(state => state.productDetails);

    return (
        <div className="mt-2">
            <Link to="/" className="ml-4">Back to results</Link>
            <div className='container mt-2'>
                <div className="row">
                    <div className="col-4">
                        <img src={"/images/" + product.image} alt=""/>
                    </div>
                    <div className="col-4">
                        <h3>
                            {product.name}
                        </h3>

                        <div>
                            {product.rating}
                        </div>

                        <div>
                            {product.numberOfReviews} customer reviews
                        </div>

                        <div>
                            Price: ${product.price}
                        </div>

                        <div>
                            State: In stock
                        </div>

                        <div>
                            <label htmlFor="Qty">Qty: </label>
                            <input className="ml-2" type="number" name="Qty" min="1" max="5"/>
                        </div>

                        <button className="btn btn-info">Add to cart</button>

                        <div>
                            Description:
                        </div>
                    </div>
                </div>

            </div>
        </div>
    )
}

export default ProductsDetail;
