import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Link} from "react-router-dom";
import axios from 'axios';

function ProductsDetail(props){
    const dispatch = useDispatch();
    useEffect( () => {
        const fetch = async () => {
            const {data} = await axios.get("/api/products/" + props.match.params.id);
            dispatch(
                {
                type: "product_detail_success",
                payload: data
            })
        }
        fetch();

        return () => {
        };
    }, [])

    const [qty, setQty] = useState(1);
    const {product} = useSelector((state) => {
        return state.productDetails
    });

    const changeQty = (e) => {
        let updatedQty = e.target.value;
        setQty(updatedQty);

        if(updatedQty > product.stock){
            document.getElementById("AddToCart").classList.add("disabled");
            document.getElementById("exceedStockAlert").style.display = "inline";
        }
        else {
            document.getElementById("AddToCart").classList.remove("disabled");
            document.getElementById("exceedStockAlert").style.display = "none";
        }
    }

    const addToCart = async (e) => {
        const {data} = await axios.get("/api/products/" + product._id);

        dispatch({
            type: "add_to_cart",
            payload: {
                productId: data._id,
                name: data.name,
                image: data.image,
                price: data.price,
                countInStock: data.countInStock,
                qty: qty
            }
        });

        props.history.push("/cart")
    }

    return <div>
        {product ? (
            <div className="mt-2">
                <Link to="/" className="ml-4">Back to results</Link>
                <div className='container mt-2'>
                    <div className="row">
                        <div className="col-4">
                            <img src={"/images/" + product.image} alt=""/>
                        </div>
                        <div className="col-4">
                            <h3>
                                {product.name}
                            </h3>

                            <div>
                                {product.rating}
                            </div>

                            <div>
                                {product.numberOfReviews} customer reviews
                            </div>

                            <div>
                                Price: ${product.price}
                            </div>

                            {
                                product.stock > 0 ?
                                    <div>Stock: {product.stock}<div>
                                    <label htmlFor="Qty">Qty: </label>
                                    <input className="ml-2" type="number" name="Qty" min="1" max="999" value={qty} onChange={(e) => {changeQty(e)}}/>
                                    </div>
                                        <button id="AddToCart" className="btn btn-info" onClick={addToCart}>Add to cart</button>
                                        <span id="exceedStockAlert" style={{display: "none"}}>Don't have that many in stock</span>
                                    </div>
                                    :
                                    <div>Out of Stock</div>
                            }
                            
                            <div>
                                Description:
                                <p>{product.description}</p>
                            </div>
                        </div>
                    </div>

                </div>
            </div>
        ) : <div>Loading...</div>}
    </div>
}

export default ProductsDetail;
